import { extendObservable } from "mobx";

class GameStore {
  constructor() {
    extendObservable(this, {
      gameState: "office",
      score: 0,
      decisions: {
        gpsText: false,
        datenhinterlegungText: false,
        keintrackingText: false,
        neinkksText: false,
        jakksText: false,
        neinkonzerneText: false,
        jakonzerneText: false,
        neinreiseText: false,
        jareiseText: false,
        neinkamerasText: false,
        jakamerasText: false,
        jagesichtserkennungText: false,
        jawaermebildText: false,
      },
    });
  }

  setGameState(value) {
    this.gameState = value;
  }

  getDecisionsAmount() {
    let count = 0;
    for (var prop in this.decisions) {
      if (Object.prototype.hasOwnProperty.call(this.decisions, prop)) {
        if (prop === true) {
          count++;
        }
      }
    }
    return count;
  }
}

export default GameStore;
