import React from "react";
import styled from "styled-components";
import { useStores } from "../util/hooks/useStores";

const Modal = styled.div`
  height: 820px;
  width: 1440px;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1000;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const CloseButton = styled.button`
  position: absolute;
  right: 30px;
  top: 30px;
`;

const RadioButton = styled.input`
  position: absolute;
  left: 20px;
  bottom: ${(props) => props.bottom}px;
`;

const label = styled.div`
  bottom: ${(props) => props.bottom}px;
`;

const Decision = styled.div`
  height: 700px;
  width: 570px;
  position: relative;
  display: inline-block;
`;

const DecisionImage = styled.img`
  height: 700px;
`;

const PaperModal = ({ closeModal, modalImage }) => {
  return (
    <Modal>
      <CloseButton onClick={() => closeModal()}>&times;</CloseButton>
      <Decision>
        <DecisionImage src={modalImage} />
        <RadioButton type="radio" bottom={150} name="abc" />
        <label for="abc">Ja</label>
        <RadioButton type="radio" bottom={120} />
        <label for="dfg">Nein</label>
      </Decision>
    </Modal>
  );
};

export default PaperModal;
